const swifdog = require("../swifdog")('max@mustermann.de', 'password');

function handleCallback(err, response) {
    if (err) throw err;
    else {
        console.log(response);
    }
}

swifdog.projects.list((err, response) => {
    if (err) throw err;
    else {
        if (response.length === 0) {
            console.log('Es existieren aktuell keine Projekte.');
        } else {
            console.log('Existierende Projekte: ' + JSON.stringify(response));

            for (let project of response) {
                console.log('LÃ¶sche projekt: ' + project.id);
                swifdog.projects.delete(project.id, handleCallback);
            }
        }
    }
});
